$('#disableNot').click(function() {
    if(localStorage.getItem(`disableNot`) === 'true') {
         localStorage.disableNot = false;
    }else if(localStorage.getItem(`disableNot`) === 'false'){
        localStorage.disableNot = true;
    }
});

if (localStorage.getItem(`disableNot`)) {
    var checked = JSON.parse(localStorage.getItem('disableNot'));
    document.getElementById("disableNot").checked = checked;
}

